int* mergeArrays(int *arr1, int size1, int *arr2, int size2) {
    int *result = (int*)malloc((size1 + size2) * sizeof(int));
    memcpy(result, arr1, size1 * sizeof(int));
    memcpy(result + size1, arr2, size2 * sizeof(int));
    return result;
}

