source_code:
#include <stdio.h>

int* filterArray(int *arr, int size, int threshold, int &newSize) {
    int *newArr = (int*)malloc(size * sizeof(int));
    newSize = 0;
    for (int i = 0; i < size; i++) {
        if (arr[i] > threshold) {
            newArr[newSize] = arr[i];
            newSize++;
        }
    }
    newArr = (int*)realloc(newArr, newSize * sizeof(int));
    return newArr;
}

target_code:
#include <iostream>
#include <vector>

std::vector<int> filterArray(const std::vector<int> &arr, int threshold) {
    std::vector<int> newArr;
    for (int i = 0; i < arr.size(); i++) {
        if (arr[i] > threshold) {
            newArr.push_back(arr[i]);
        }
    }
    return newArr;
}

