#include <stdio.h>
#include <stdlib.h>
void sumar_matrices(int** mat1, int** mat2, int** resultado, int filas, int columnas) {
    *resultado = (int**)malloc(filas * sizeof(int*));
    for (int i = 0; i < filas; i++) {
        (*resultado)[i] = (int*)malloc(columnas * sizeof(int));
        for (int j = 0; j < columnas; j++) {
            (*resultado)[i][j] = mat1[i][j] + mat2[i][j];
        }
    }
}
int main() {
    int** mat1 = (int**)malloc(2 * sizeof(int*));
    int** mat2 = (int**)malloc(2 * sizeof(int*));
    int** resultado;
    for (int i = 0; i < 2; i++) {
        mat1[i] = (int*)malloc(2 * sizeof(int));
        mat2[i] = (int*)malloc(2 * sizeof(int));
        for (int j = 0; j < 2; j++) {
            mat1[i][j] = i + j;
            mat2[i][j] = i - j;
        }
    }
    sumar_matrices(mat1, mat2, &resultado, 2, 2);
    for (int i = 0; i < 2; i++) {
        for (int j = 0; j < 2; j++) {
            printf("%d ", resultado[i][j]);
        }
        printf("\n");
    }
    return 0;
}

