    resultado.resize(mat1.size(), std::vector<int>(mat1[0].size()));
    for (int i = 0; i < mat1.size(); i++) {
        for (int j = 0; j < mat1[0].size(); j++) {
            resultado[i][j] = mat1[i][j] + mat2[i][j];
        }
    }
}
int main() {
    std::vector<std::vector<int>> mat1 = {{0, 1}, {1, 2}};
