#include <stdio.h>
#include <stdlib.h>

void multiply_matrix(int **matrix, int rows, int cols, int scalar) {
    for (int i = 0; i < rows; i++) {
        for (int j = 0; j < cols; j++) {
            matrix[i][j] *= scalar;
        }
    }
}

int main() {
    int **matrix = (int**)malloc(2 * sizeof(int*));
    for (int i = 0; i < 2; i++) {
        matrix[i] = (int*)malloc(3 * sizeof(int));
        for (int j = 0; j < 3; j++) {
            matrix[i][j] = i + j;
        }
    }
    multiply_matrix(matrix, 2, 3, 2);
    for (int i = 0; i < 2; i++) {
        for (int j = 0; j < 3; j++) {
            printf("%d ", matrix[i][j]);
        }
        printf("\n");
    }
    for (int i = 0; i < 2; i++) {
        free(matrix[i]);
    }
    free(matrix);
    return 0;
}

