#include <stdio.h>

void uppercase(char* str) {
    while (*str) {
        if (*str >= 'a' && *str <= 'z') {
            *str = *str - 'a' + 'A';
        }
        str++;
    }
}

int main() {
    char str[] = "hello world";
    uppercase(str);
    printf("Uppercase string: %s\n", str);
    return 0;
}

