#include <stdio.h>
#include <stdlib.h>

void reverse_array(int* arr, int size) {
    int* temp = (int*)malloc(size * sizeof(int));
    for (int i = 0; i < size; i++) {
        temp[size - i - 1] = arr[i];
    }
    for (int i = 0; i < size; i++) {
        arr[i] = temp[i];
    }
    free(temp);
}

int main() {
    int arr[] = {1, 2, 3, 4, 5};
    reverse_array(arr, 5);
    for (int i = 0; i < 5; i++) {
        printf("%d ", arr[i]);
    }
    return 0;
}

