#include <stdio.h>
#include <stdlib.h>

void reverseArray(int* array, int n) {
    int left = 0;
    int right = n - 1;
    while(left < right) {
        int temp = array[left];
        array[left] = array[right];
        array[right] = temp;
        left++;
        right--;
    }
}

int main() {
    int n = 5;
    int* array = (int*)malloc(n * sizeof(int));
    for(int i = 0; i < n; i++) {
        array[i] = i + 1;
    }
    reverseArray(array, n);
    for(int i = 0; i < n; i++) {
        printf("%d ", array[i]);
    }
    free(array);
    return 0;
}

