#include <stdio.h>

void invertir_array(int* array, int longitud) {
    int i = 0, j = longitud - 1;
    while (i < j) {
        int temp = array[i];
        array[i] = array[j];
        array[j] = temp;
        i++;
        j--;
    }
}

int main() {
    int numeros[] = {1, 2, 3, 4, 5};
    invertir_array(numeros, 5);
    for (int i = 0; i < 5; i++) {
        printf("%d ", numeros[i]);
    }
    return 0;
}

