#include <stdio.h>
#include <stdlib.h>

void multiplyMatrix(int **matrix, int rows, int cols, int scalar) {
    for(int i = 0; i < rows; i++) {
        for(int j = 0; j < cols; j++) {
            matrix[i][j] *= scalar;
        }
    }
}

int main() {
    int rows = 2;
    int cols = 2;
    int **matrix = (int **)malloc(rows * sizeof(int *));
    for(int i = 0; i < rows; i++) {
        matrix[i] = (int *)malloc(cols * sizeof(int));
        for(int j = 0; j < cols; j++) {
            matrix[i][j] = i + j;
        }
    }
    multiplyMatrix(matrix, rows, cols, 2);
    for(int i = 0; i < rows; i++) {
        for(int j = 0; j < cols; j++) {
            printf("%d ", matrix[i][j]);
        }
        printf("\n");
    }
    for(int i = 0; i < rows; i++) {
        free(matrix[i]);
    }
    free(matrix);
    return 0;
}

