source_code:
#include <stdio.h>

void multiplyMatrix(int **mat1, int **mat2, int **result, int rows, int cols) {
	for(int i = 0; i < rows; i++) {
		for(int j = 0; j < cols; j++) {
			result[i][j] = 0;
			for(int k = 0; k < cols; k++) {
				result[i][j] += mat1[i][k] * mat2[k][j];
			}
		}
	}
}

int main() {
	int rows = 2, cols = 2;
	int **mat1, **mat2, **result;
	mat1 = (int **)malloc(rows * sizeof(int *));
	mat2 = (int **)malloc(rows * sizeof(int *));
	result = (int **)malloc(rows * sizeof(int *));

	for(int i = 0; i < rows; i++) {
		mat1[i] = (int *)malloc(cols * sizeof(int));
		mat2[i] = (int *)malloc(cols * sizeof(int));
		result[i] = (int *)malloc(cols * sizeof(int));
	}

	multiplyMatrix(mat1, mat2, result, rows, cols);

	for(int i = 0; i < rows; i++) {
		free(mat1[i]);
		free(mat2[i]);
		free(result[i]);
	}

	free(mat1);
	free(mat2);
	free(result);

	return 0;
}

target_code:
#include <iostream>
#include <vector>

void multiplyMatrix(std::vector<std::vector<int>> &mat1, std::vector<std::vector<int>> &mat2, std::vector<std::vector<int>> &result) {
	int rows = mat1.size();
	int cols = mat2[0].size();

	result.resize(rows, std::vector<int>(cols, 0));

	for(int i = 0; i < rows; i++) {
		for(int j = 0; j < cols; j++) {
			for(int k = 0; k < mat1[0].size(); k++) {
				result[i][j] += mat1[i][k] * mat2[k][j];
			}
		}
	}
}

int main() {
	std::vector<std::vector<int>> mat1 = {{1, 2}, {3, 4}};
	std::vector<std::vector<int>> mat2 = {{1, 0}, {0, 1}};
	std::vector<std::vector<int>> result;

	multiplyMatrix(mat1, mat2, result);

	return 0;
}

