#include <iostream>
#include <vector>

void reverseVector(std::vector<int> &vec) {
    std::vector<int> temp;
    for(int i = vec.size() - 1; i >= 0; i--) {
        temp.push_back(vec[i]);
    }
    vec = temp;
}

int main() {
    std::vector<int> vec = {1, 2, 3, 4, 5};

    reverseVector(vec);

    for(int num : vec) {
        std::cout << num << ' ';
    }

    return 0;
}

