#include <stdio.h>

void copy_string(char *dest, const char *src) {
    while(*src) {
        *dest = *src;
        dest++;
        src++;
    }
    *dest = '\0';
}

int main() {
    char src[] = "Hello, World!";
    char dest[20];
    copy_string(dest, src);
    printf("Copied string: %s\n", dest);
    return 0;
}

