#include <iostream>
#include <vector>

void reverse_vector(std::vector<int>& vec) {
    std::vector<int> temp;
    for (int i = vec.size() - 1; i >= 0; i--) {
        temp.push_back(vec[i]);
    }
    vec = temp;
}

int main() {
    std::vector<int> vec = {1, 2, 3, 4, 5};
    reverse_vector(vec);
    for (int num : vec) {
        std::cout << num << " ";
    }
    std::cout << std::endl;
    return 0;
}

