#include <stdio.h>
#include <stdlib.h>

void reverse_array(int *arr, int size) {
    int *temp = (int *)malloc(size * sizeof(int));
    for(int i = 0; i < size; i++) {
        temp[i] = arr[size - i - 1];
    }
    for(int i = 0; i < size; i++) {
        arr[i] = temp[i];
    }
    free(temp);
}

int main() {
    int arr[] = {1, 2, 3, 4, 5};
    reverse_array(arr, 5);
    for(int i = 0; i < 5; i++) {
        printf("%d ", arr[i]);
    }
    return 0;
}

target_code:
#include <iostream>
#include <vector>

void reverse_vector(std::vector<int> &vec) {
    std::vector<int> temp;
    for(int i = vec.size() - 1; i >= 0; i--) {
        temp.push_back(vec[i]);
    }
    vec = temp;
}

int main() {
    std::vector<int> vec = {1, 2, 3, 4, 5};
    reverse_vector(vec);
    for(int i = 0; i < vec.size(); i++) {
        std::cout << vec[i] << " ";
    }
    return 0;
}

