#include <stdlib.h>

void c_function3(int** matrix, int rows, int cols){
    for(int i = 0; i < rows; i++){
        for(int j = 0; j < cols; j++){
            matrix[i][j] *= 2;
        }
    }
}

int main(){
    int** matrix = (int**)malloc(3 * sizeof(int*));
    for(int i = 0; i < 3; i++){
        matrix[i] = (int*)malloc(3 * sizeof(int));
        for(int j = 0; j < 3; j++){
            matrix[i][j] = i + j;
        }
    }
    c_function3(matrix, 3, 3);
    for(int i = 0; i < 3; i++){
        for(int j = 0; j < 3; j++){
            printf("%d ", matrix[i][j]);
        }
        printf("\n");
    }
    return 0;
}

