#include <stdio.h>
#include <stdlib.h>

void c_reverse_string(char *str) {
    int length = 0;
    while (str[length] != '\0') {
        length++;
    }
    for (int i = 0; i < length / 2; i++) {
        char temp = str[i];
        str[i] = str[length - i - 1];
        str[length - i - 1] = temp;
    }
}

int main() {
    char str[] = "hello";
    c_reverse_string(str);
    printf("Reversed string in C: %s\n", str);
    return 0;
}

