#include <stdio.h>
#include <stdlib.h>

void reverse_array(int* arr, int size) {
    int start = 0;
    int end = size - 1;
    while (start < end) {
        int temp = arr[start];
        arr[start] = arr[end];
        arr[end] = temp;
        start++;
        end--;
    }
}

int main() {
    int arr[] = {1, 2, 3, 4, 5};
    int size = 5;
    reverse_array(arr, size);
    for (int i = 0; i < size; i++) {
        printf("%d ", arr[i]);
    }
    return 0;
}

target_code:
#include <iostream>
#include <vector>

void reverse_vector(std::vector<int>& vec) {
    int start = 0;
    int end = vec.size() - 1;
    while (start < end) {
        std::swap(vec[start], vec[end]);
        start++;
        end--;
    }
}

int main() {
    std::vector<int> vec = {1, 2, 3, 4, 5};
    reverse_vector(vec);
    for (int num : vec) {
        std::cout << num << " ";
    }
    return 0;
}

