#include <stdio.h>

void reverse_string(char* str) {
    int length = 0;
    while (str[length] != '\0') {
        length++;
    }
    for (int i = 0; i < length / 2; i++) {
        char temp = str[i];
        str[i] = str[length - i - 1];
        str[length - i - 1] = temp;
    }
}

int main() {
    char my_string[] = "hello";
    reverse_string(my_string);
    printf("Reversed string: %s\n", my_string);
    return 0;
}

