#include <stdio.h>

void copy_string(char *dest, char *src) {
    while (*src) {
        *dest = *src;
        dest++;
        src++;
    }
    *dest = '\0';
}

int main() {
    char str1[10], str2[] = "Hello";
    copy_string(str1, str2);
    printf("Copied string: %s\n", str1);
    return 0;
}

