#include <stdlib.h>

int* duplicarArray(int *array, int size) {
    int *nuevoArray = (int*)malloc(size * sizeof(int));
    for (int i = 0; i < size; i++) {
        nuevoArray[i] = array[i] * 2;
    }
    return nuevoArray;
}

int main() {
    int arr[] = {1, 2, 3, 4, 5};
    int *nuevoArr = duplicarArray(arr, 5);
    for (int i = 0; i < 5; i++) {
        printf("%d ", nuevoArr[i]);
    }
    free(nuevoArr);
    return 0;
}

