#include <stdio.h>
#include <stdlib.h>

void c_function_merge_arrays(int *array1, int size1, int *array2, int size2, int *result) {
    int i = 0, j = 0, k = 0;
    while (i < size1 && j < size2) {
        if (array1[i] <= array2[j]) {
            result[k++] = array1[i++];
        } else {
            result[k++] = array2[j++];
        }
    }
    while (i < size1) {
        result[k++] = array1[i++];
    }
    while (j < size2) {
        result[k++] = array2[j++];
    }
}

