    int i = 0, j = 0;
    while (i < vec1.size() && j < vec2.size()) {
        if (vec1[i] <= vec2[j]) {
            result.push_back(vec1[i++]);
        } else {
            result.push_back(vec2[j++]);
        }
    }
    while (i < vec1.size()) {
        result.push_back(vec1[i++]);
    }
    while (j < vec2.size()) {
        result.push_back(vec2[j++]);
    }
    return result;
}

