source_code:
#include <stdio.h>
#include <stdlib.h>

void c_multiply(int *mat1, int *mat2, int *result, int rows, int cols, int common) {
    int i, j, k;
    for (i = 0; i < rows; i++) {
        for (j = 0; j < cols; j++) {
            result[i * cols + j] = 0;
            for (k = 0; k < common; k++) {
                result[i * cols + j] += mat1[i * common + k] * mat2[k * cols + j];
            }
        }
    }
}

int main() {
    int mat1[] = {2, 3, 4, 5};
    int mat2[] = {1, 0, 2, 1};
    int result[4];
    c_multiply(mat1, mat2, result, 2, 2, 2);
    for (int i = 0; i < 2; i++) {
        for (int j = 0; j < 2; j++) {
            printf("%d ", result[i * 2 + j]);
        }
        printf("\n");
    }
    return 0;
}

