#include <stdlib.h>

void* c_function4(int size) {
    return malloc(size);
}

int main() {
    int* ptr = (int*)c_function4(5 * sizeof(int));
    for (int i = 0; i < 5; i++) {
        ptr[i] = i + 1;
        printf("C Malloc[%d]: %d\n", i, ptr[i]);
    }
    free(ptr);
    return 0;
}

