#include <stdio.h>

void c_multiply_matrix(int mat[2][2], int num) {
    for (int i = 0; i < 2; i++) {
        for (int j = 0; j < 2; j++) {
            mat[i][j] *= num;
        }
    }
}

int main() {
    int mat[2][2] = {{1, 2}, {3, 4}};
    c_multiply_matrix(mat, 2);
    for (int i = 0; i < 2; i++) {
        for (int j = 0; j < 2; j++) {
            printf("%d ", mat[i][j]);
        }
        printf("\n");
    }
    return 0;
}

