void reverseArray(int *arr, int size) {
    int *temp = (int *) malloc(size * sizeof(int));
    for (int i = 0; i < size; i++) {
        temp[i] = arr[size - i - 1];
    }
    for (int i = 0; i < size; i++) {
        arr[i] = temp[i];
    }
    free(temp);
}

int main() {
    int arr[] = {1, 2, 3, 4, 5};
    reverseArray(arr, 5);
    for (int i = 0; i < 5; i++) {
        printf("%d ", arr[i]);
    }
    return 0;
}

