#include <stdio.h>
#include <stdlib.h>

void sumar_matrices(int **m1, int **m2, int **resultado, int filas, int columnas) {
    for (int i = 0; i < filas; i++) {
        for (int j = 0; j < columnas; j++) {
            resultado[i][j] = m1[i][j] + m2[i][j];
        }
    }
}

int main() {
    int **m1 = (int **)malloc(2 * sizeof(int *));
    int **m2 = (int **)malloc(2 * sizeof(int *));
    int **resultado = (int **)malloc(2 * sizeof(int *));
    for (int i = 0; i < 2; i++) {
        m1[i] = (int *)malloc(2 * sizeof(int));
        m2[i] = (int *)malloc(2 * sizeof(int));
        resultado[i] = (int *)malloc(2 * sizeof(int));
    }
    m1[0][0] = 1; m1[0][1] = 2; m1[1][0] = 3; m1[1][1] = 4;
    m2[0][0] = 5; m2[0][1] = 6; m2[1][0] = 7; m2[1][1] = 8;
    sumar_matrices(m1, m2, resultado, 2, 2);
    for (int i = 0; i < 2; i++) {
        for (int j = 0; j < 2; j++) {
            printf("%d ", resultado[i][j]);
        }
        printf("\n");
    }
    for (int i = 0; i < 2; i++) {
        free(m1[i]);
        free(m2[i]);
        free(resultado[i]);
    }
    free(m1); free(m2); free(resultado);
    return 0;
}

