#include <stdio.h>
#include <stdlib.h>

void multiplyMatrix(int** matrix, int rows, int cols, int** result) {
    for(int i = 0; i < rows; i++) {
        for(int j = 0; j < cols; j++) {
            result[i][j] = 0;
            for(int k = 0; k < cols; k++) {
                result[i][j] += matrix[i][k] * matrix[k][j];
            }
        }
    }
}

int main() {
    int** matrix = (int**)malloc(2 * sizeof(int*));
    for(int i = 0; i < 2; i++) {
        matrix[i] = (int*)malloc(2 * sizeof(int));
    }
    matrix[0][0] = 1;
    matrix[0][1] = 2;
    matrix[1][0] = 3;
    matrix[1][1] = 4;
    int** result = (int**)malloc(2 * sizeof(int*));
    for(int i = 0; i < 2; i++) {
        result[i] = (int*)malloc(2 * sizeof(int));
    }
    multiplyMatrix(matrix, 2, 2, result);
    for(int i = 0; i < 2; i++) {
        for(int j = 0; j < 2; j++) {
            printf("%d ", result[i][j]);
        }
        printf("\n");
    }
    return 0;
}

