source_code:
#include <stdio.h>

void reverse_array(int* arr, int size) {
    int start = 0;
    int end = size - 1;
    while (start < end) {
        int temp = arr[start];
        arr[start] = arr[end];
        arr[end] = temp;
        start++;
        end--;
    }
}

int main() {
    int arr[] = {1, 2, 3, 4, 5};
    reverse_array(arr, 5);
    for (int i = 0; i < 5; i++) {
        printf("%d ", arr[i]);
    }
    printf("\n");
    return 0;
}

target_code:
#include <iostream>
#include <vector>

void reverse_vector(std::vector<int>& vec) {
    int start = 0;
    int end = vec.size() - 1;
    while (start < end) {
        int temp = vec[start];
        vec[start] = vec[end];
        vec[end] = temp;
        start++;
        end--;
    }
}

int main() {
    std::vector<int> vec = {1, 2, 3, 4, 5};
    reverse_vector(vec);
    for (int i = 0; i < vec.size(); i++) {
        std::cout << vec[i] << " ";
    }
    std::cout << std::endl;
    return 0;
}

