#include <stdio.h>

int* add_arrays(int *arr1, int *arr2, int size) {
    int *result = (int*)malloc(size * sizeof(int));
    for (int i = 0; i < size; i++) {
        result[i] = arr1[i] + arr2[i];
    }
    return result;
}

int main() {
    int size = 3;
    int arr1[] = {1, 2, 3};
    int arr2[] = {4, 5, 6};
    int *result = add_arrays(arr1, arr2, size);
    for (int i = 0; i < size; i++) {
        printf("%d ", result[i]);
    }
    free(result);
    return 0;
}

