std::vector<int> add_arrays(const std::vector<int> &arr1, const std::vector<int> &arr2) {
    std::vector<int> result(arr1.size());
    for (size_t i = 0; i < arr1.size(); i++) {
        result[i] = arr1[i] + arr2[i];
    }
    return result;
}

int main() {
    std::vector<int> arr1 = {1, 2, 3};
    std::vector<int> arr2 = {4, 5, 6};
    std::vector<int> result = add_arrays(arr1, arr2);
