#include <stdio.h>
#include <stdlib.h>

void mergeArrays(int *arr1, int size1, int *arr2, int size2, int *result) {
    int i = 0, j = 0, k = 0;
    while (i < size1 && j < size2) {
        if (arr1[i] <= arr2[j]) {
            result[k++] = arr1[i++];
        } else {
            result[k++] = arr2[j++];
        }
    }
    while (i < size1) {
        result[k++] = arr1[i++];
    }
    while (j < size2) {
        result[k++] = arr2[j++];
    }
}

int main() {
    int arr1[] = {1, 3, 5};
    int arr2[] = {2, 4, 6, 8};
    int result[7];
    mergeArrays(arr1, 3, arr2, 4, result);
    printf("Result in C: ");
    for (int i = 0; i < 7; i++) {
        printf("%d ", result[i]);
    }
    printf("\n");
    return 0;
}

