#include <stdio.h>
#include <stdlib.h>

void c_function5(int **matrix, int rows, int cols) {
    for(int i = 0; i < rows; i++) {
        for(int j = 0; j < cols; j++) {
            matrix[i][j] *= 2;
        }
    }
}

int main() {
    int **matrix;
    int rows = 2;
    int cols = 3;
    matrix = (int **)malloc(rows * sizeof(int *));
    for(int i = 0; i < rows; i++) {
        matrix[i] = (int *)malloc(cols * sizeof(int));
        for(int j = 0; j < cols; j++) {
            matrix[i][j] = i + j;
        }
    }
    c_function5(matrix, rows, cols);
    for(int i = 0; i < rows; i++) {
        for(int j = 0; j < cols; j++) {
            printf("%d ", matrix[i][j]);
        }
        printf("\n");
    }
    return 0;
}

