#include <stdio.h>
#include <stdlib.h>

void copyString(const char* src, char* dest) {
    while (*src != '\0') {
        *dest = *src;
        src++;
        dest++;
    }
    *dest = '\0';
}

int main() {
    const char* src = "Hello, World!";
    char* dest = (char*)malloc(strlen(src) + 1);
    copyString(src, dest);
    printf("Copied string: %s\n", dest);
    free(dest);
    return 0;
}

