#include <stdio.h>
#include <stdlib.h>

int* mergeArrays(int *arr1, int size1, int *arr2, int size2) {
    int *mergedArr = (int *)malloc((size1 + size2) * sizeof(int));
    for (int i = 0; i < size1; i++) {
        mergedArr[i] = arr1[i];
    }
    for (int j = 0; j < size2; j++) {
        mergedArr[size1 + j] = arr2[j];
    }
    return mergedArr;
}

target_code:
#include <iostream>
#include <vector>

std::vector<int> mergeArrays(const std::vector<int> &arr1, const std::vector<int> &arr2) {
    std::vector<int> mergedArr;
    mergedArr.reserve(arr1.size() + arr2.size());
    mergedArr.insert(mergedArr.end(), arr1.begin(), arr1.end());
    mergedArr.insert(mergedArr.end(), arr2.begin(), arr2.end());
    return mergedArr;
}

