int* filterArray(int *arr, int size, int threshold, int &newSize) {
    int *newArr = (int*)malloc(size * sizeof(int));
    newSize = 0;
    for (int i = 0; i < size; i++) {
        if (arr[i] > threshold) {
            newArr[newSize] = arr[i];
            newSize++;
        }
    }
    newArr = (int*)realloc(newArr, newSize * sizeof(int));
    return newArr;
}

