#include <stdio.h>
#include <stdlib.h>

char** split_string(const char* str, char delimiter) {
    size_t count = 0;
    for (const char* tmp = str; *tmp; ++tmp) {
        count += (*tmp == delimiter);
    }
    char** result = (char**)malloc((count + 2) * sizeof(char*));
    size_t index = 0;
    const char* start = str;
    for (const char* tmp = str; ; ++tmp) {
        if (*tmp == delimiter || *tmp == '\0') {
            size_t length = tmp - start;
            result[index] = (char*)malloc(length + 1);
            strncpy(result[index], start, length);
            result[index][length] = '\0';
            index++;
            if (*tmp == '\0') break;
            start = tmp + 1;
        }
    }
    result[index] = NULL;
    return result;
}

