char* join_strings(char** strings, int count, const char* delim) {
    size_t length = 0;
    for (int i = 0; i < count; ++i) {
        length += strlen(strings[i]) + strlen(delim);
    }
    char* result = (char*)malloc(length + 1);
    result[0] = '\0';
    for (int i = 0; i < count; ++i) {
        strcat(result, strings[i]);
        if (i < count - 1) {
            strcat(result, delim);
        }
    }
    return result;
}

